function think(selector){
    let self = document.querySelector(selector);

    self.text = function(text){
        if(text){
            self.innerText = text;
        }
        return self.innerText;
    }
    self.html = function(html){
        if(html){
            self.innerHTML = html;
        }
        return self.innerHTML;
    }
    self.css = function(css){
        for(let key in css){
            self.style[key] = css[key];
        }
    }
    self.on = function(event, callback){
        self.addEventListener(event, callback);
    }
    self.click = function(callback){
        self.addEventListener("click", callback)
    }
    self.mouseover = function(callback){
        self.addEventListener("mouseover", callback)
    }
    self.addclass = function(classname){
        self.classList.add(classname);
    }
    self.removeclass = function(classname){
        self.classList.remove(classname);
    }

    return self;
}